import React, { useState } from 'react';

function About() {
    const [value, setValue] = useState();
    const [checked, setChecked] = useState(false);

    function handleChange(event) {
        if (event.target.name == 'estimate') {
            setValue(event.target.value);
        }
    }

    function handleCheck(event) {
        if (event.target.name == 'agree') {
            setChecked(true)
        }
    }

    function handleClick() {
        let estimate = document.getElementsByClassName('estimate');
        console.log(isNaN(estimate.value));
        if (estimate.value === '' || estimate.value == ' ') {
            alert('Please estimate the value of your bike.')
        }
        if (!isNaN(estimate.value)) {
            alert('Estimated value must be a numerical value.')
        }
        if (checked == false) {
            alert('You must agree to the Terms of Service & Privacy Policy to proceed.');
        }
        if (isNaN(estimate.value) && estimate.value != '' && estimate.value != ' ' && checked == true) {
            document.location.href = '/signup2';
        }
    }

    return (
        <section class="sign-up">
            <div class="form-content">
                <div class="about">
                    <h4>Tell us about your bike!</h4>
                    <form class="uk-form-horizontal uk-margin-large">
                        <div class="uk-margin">
                            <label class="uk-form-label" for="form-horizontal-select">Make</label>
                            <div class="uk-form-controls">
                                <select class="uk-select" id="make form-horizontal-select">
                                    <option>Option 01</option>
                                    <option>Option 02</option>
                                </select>
                            </div>
                        </div>
                        <div class="uk-margin">
                            <label class="uk-form-label" for="form-horizontal-select">Model</label>
                            <div class="uk-form-controls">
                                <select class="uk-select" id="model form-horizontal-select">
                                    <option>Option 01</option>
                                    <option>Option 02</option>
                                </select>
                            </div>
                        </div>
                        <div class="uk-margin">
                            <label class="uk-form-label" for="form-horizontal-select">Year</label>
                            <div class="uk-form-controls">
                                <select class="uk-select" id="year form-horizontal-select">
                                    <option>Option 01</option>
                                    <option>Option 02</option>
                                </select>
                            </div>
                        </div>
                    </form>
                    <div class="row uk-margin">
                        <div class="col-md-6">
                            <p>Estimated Value:</p>
                        </div>
                        <div class="col-md-4">
                            <div class="uk-inline">
                                <span class="uk-form-icon">$</span>
                                <input class="estimate uk-input" type="text" name="value" value={value} onChange={handleChange}></input>
                            </div>
                        </div>
                    </div>
                    <div class="terms">
                        <p>Terms of Service <a href="#">View</a></p>
                        <p>Privacy Policy <a href="#">View</a></p>
                        <label><input class="agree uk-checkbox" type="checkbox" name="agree" checked={checked} onClick={handleCheck}></input> Agree to Terms of Service & Privacy
                        Policy*</label>
                    </div>
                    <button class="next1-btn uk-button uk-button-default" onClick={handleClick}>Next</button>
                </div>
            </div>
        </section>
    )
};

export default About;
